INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (30, 'Xtra Alga', 'Bionutrientes', 'Bioestimulante', 'RSCO-163/VII/09', 'Nitrógeno (N) 1.120%, Sodio (Na) 1.260%, Fósforo (P₂O₅) 0.8200%, Manganeso (Mn) 0.0004%, Potasio (K₂O) 5.6600%, Hierro (Fe) 0.0028%, Calcio (Ca) 0.2400%, Zinc (Zn) 0.0007%, Magnesio (Mg) 0.1400%, Boro (B) 0.1200%, Azufre (S) 0.2300%. Líquido.', 'Hortalizas', '0.5 a 2 L/ha. 5 a 10 ml/L de agua', 'Foliar: 7 a 21 días. Fertirriego: 21 a 30 días', 'Efecto: Hormonal, antioxidante, antibiosis natural, acción coloidal y síntesis de proteínas. Fortalece el sistema inmunológico y alimentario, activando sus funciones fisiológicas (aumento en clorofila y capacidad fotosintética) que se traduce en crecimiento y desarrollo equilibrado. Estimula los mecanismos de defensa natural de la planta contra enfermedades fungosas. Proporciona tolerancia contra el estrés ambiental.', '/fichasTecnica/XtraAlga.pdf', '/hojasSeguridad/XtraAlga.pdf', '/FotosProducto/XtraAlga.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/XtraAlga.png');
