INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (32, 'Nutripro KMg', 'Bionutrientes', 'Biofertilizante Organo Mineral', 'RSCO-163/VII/09', 'Nitrógeno total (N): 1.0%, Potasio (K₂O): 40.0%, Azufre (SO₃): 45.0%, Magnesio (MgO): 2.1%. Polvo.', 'Jitomate, chile, sandía, melón, calabacita, pepino, fresa, papa y ornamentales.', 'Foliar: 4.0 a 6.0 g/L agua. Fertirriego: 5 a 10 kg/ha.', 'Foliar: Cada 7-14 días. Fertirriego: A partir de trasplante.', 'Efecto: Fertilizante organo-mineral en polvo que contiene nitrógeno, potasio, azufre, magnesio, aminoácidos y materia orgánica. Previene y corrige los síntomas causados por la deficiencia de nitrógeno, potasio, azufre y magnesio. Los aminoácidos totales y materia orgánica facilita la penetración y translocación de los nutrientes al interior de la planta, ayudando al cultivo en el desarrollo vegetativo y favorece el amarre y cuajado de los frutos.', '/fichasTecnica/NutriproKMg.pdf', '/hojasSeguridad/NutriproKMg.pdf', '/FotosProducto/NutriproKMg.png', '20 x 1 Kg.', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproKMg.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (33, 'Nutripro CaB', 'Bionutrientes', 'Biofertilizante Organo Mineral', 'RSCO-222/XI/09', 'Calcio (CaO): 9.0%, Magnesio (MgO): 2.0%, Boro (B): 1.0%, Azufre (S): 3.5%. Líquido.', 'Jitomate, chile, sandía, melón, calabacita, pepino, fresa, papa, lechuga, col, coliflor, vid, algodón, cítricos, manzano, durazno, ciruelo, peral, aguacate, maíz y trigo.', 'Foliar: 2.5 a 3.5 ml/L de agua. Fertirriego: 2.0 a 4.0 L/ha.', '7 a 14 días.', 'Efecto: Es un fertilizante organo-mineral líquido que contiene nitrógeno, calcio,magnesio, boro y aminoácidos libres. Por su contenido de calcio ayuda directamente en el crecimiento apical, consistencia de la estructura de la planta y la calidad de los frutos después del amarre., previene y corrige los síntomas causados por la deficiencia de nitrógeno, calcio, magnesio y boro, los aminoácidos libres ayudan a que la planta asimile los nutrientes disponibles, ayuda al cultivo en cuajado y desarrollo del fruto.', '/fichasTecnica/NutriproCaB.pdf', '/hojasSeguridad/NutriproCaB.pdf', '/FotosProducto/NutriproCaB.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproCaB.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (34, 'Energy', 'Bionutrientes', 'Bioestimulante', 'RSCO-146/XII/20', 'Nitrógeno Orgánico: (16.00%). Carbono Orgánico: (44.00%). Micro-gránulos hidrosolubles.', 'Aguacate berenjena, chile, jitomate, okra, papa, pimiento morrón, tomate, arándano, frambuesa, fresa, grosella, zarzamora, zarzaparrilla, calabaza, calabacita, chayote, melón, pepino, sandía.', '5 a 20 kg/ha.', 'Realizar dos aplicaciones dirigidas al suelo en la etapa de desarrollo vegetativo y floración.', 'Efecto: Nutriente Vegetal formulado en microgránulos altamente solubles en agua que contiene nitrógeno orgánico y aminoácidos activos que permiten una respuesta rápida de los cultivos. Está diseñado para aportar nitrógeno en las etapas de mayor demanda del cultivo, para estimular el desarrollo radicular y vegetal, así como el calibre y calidad de los frutos.', '/fichasTecnica/NutriproEnergy.pdf', '/hojasSeguridad/NutriproEnergy.pdf', '/FotosProducto/NutriproEnergy.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproEnergy.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (35, 'KBorCa', 'Bionutrientes', 'Fertilizante Foliar', 'RSCO-222/XI/09', 'Calcio (Ca-EDTA): 5.31%, Boro (B): 1.43%, Potasio (K2O) 13.38%, Nitrógeno total (N): 3.26%. Líquido soluble.', 'Berries, hortalizas, frutales, granos, forrajes y ornamentales.', 'Berries y Hortalizas 1 - 2 L/ha. Frutales 2- 4 L/ha. Granos y Forrajes 1 - 1.5 L/ha. Ornamentales 50 - 70 ml/100 L de agua.', '7 a 14 días.', 'Efecto: Es un nutriente vegetal que contiene calcio quelado, boro, potasio y nitrógeno, recomendado para prevenir trastornos fisiológicos asociados con su deficiencia. Promueve vigor en las plantas, tallos fuertes, reduce el aborto de flores y frutos, promueve la floración y el amarre de frutos. Mejora la calidad de los frutos , maduración, firmeza y vida de anaquel.', '/fichasTecnica/KBorCa.pdf', '/hojasSeguridad/KBorCa.pdf', '/FotosProducto/KBorCa.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/KBorCa.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (36, 'Aminofit:Xtra', 'Bionutrientes', 'Aminoacidos', 'RSCO-075/XI/04', 'Aminoácidos: Glicina 800 mg, Valina 40 mg, Prolina 350 mg, Hidroxiprolina 300 mg, Alanina 350 mg, Ác. Aspártico 680 mg, Arginina 80 mg, Ác. Glutámico 225 mg, Lisina 345 mg, Leucina 50 mg, Isoleucina 125 mg, Fenilalanina 145 mg, Metionina 25 mg, Serina 140 mg, Asparagina 125 mg, Treonina 120 mg, Cistina y Cisteina 85 mg, Tirosina 88 mg, Histidina 102 mg. Solución Acuosa.', 'Frutales, ornamentales y hortalizas.', 'Vía foliar: 2.5-5 L/ha. Suelo: 5-15 L/ha/Ciclo', 'Durante todo el ciclo del cultivo y de 2 a 3 semanas.', 'Efecto: Síntesis de proteínas que desarrollan funciones estructurales, enzimáticas y hormonales, relacionadas con los mecanismos de regulación del crecimiento y desarrollo vegetal. Forma quelatos con microelementos favoreciendo su transporte y asimilación. Recupera daños en cultivos.', '/fichasTecnica/Aminoacidos.pdf', '/hojasSeguridad/Aminoacidos.pdf', '/FotosProducto/Aminoacidos.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Aminoacidos.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (37, 'Activador', 'Bionutrientes', 'Regulador de crecimiento', 'RSCO-150/VIII/19', 'Ácido giberélico 1,451.00 ppm, Ácido-3-indolacético 4,070.00 ppm, Ácido glutámico 1,117.00 ppm, Aminoácidos 0.737 ppm, Niacina 1,048.10 ppm, Tiamina 1,019.27 ppm, Polisacáridos, Glucosa 2,051.70 ppm, Fructosa 1,798.50 ppm, Galactosa 279.48 ppm, Fósforo (P₂O₅) 1,052.10 ppm, Potasio (K₂O) 876.20 ppm, Nitrógeno total (N) 12.21%. Líquido soluble.', 'Calabaza, calabacita, chayote, melón, pepino, sandía, berenjena, chile, chile bel, jitomate, papa, tabaco, tomate de cáscara.', '500 - 750 mL/ha', 'Aplicaciones cada 15 días desde la aparición del botón floral hasta el llenado de frutos.', 'Efecto: Es un regulador de crecimiento que interviene en los procesos metabólicos y fisiológicos de las plantas: división celular, síntesis de clorofila, aporte de energía y sustancias nutritivas. Aplíquelo para estimular la condición nutrimental de la planta, el volumen radicular, la altura de planta, biomasa seca de la planta', '/fichasTecnica/Activador.pdf', '/hojasSeguridad/Activador.pdf', '/FotosProducto/Activador.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Activador.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (38, 'Macroroot', 'Bionutrientes', 'Regulador de crecimiento', 'RSCO-030/IV/20', 'Ácido Naftalenacético 2812 ppm, Ácido Indolbutírico 209 ppm, Extracto de Algas 5.243% p/p, Fósforo (P₂O₅) 55.108% p/p, Nitrógeno total (N) 4.72% p/p. Polvo soluble.', 'Plátano, banano, berenjena, chile, chile bel, jitomate, papa, tabaco, tomate de cáscara.', '1.0 - 1.5 kg/ha', 'Aplicaciones a partir de la brotación a intervalo de 4 semanas.', 'Efecto: Es un regulador de crecimiento que contiene fitohormonas, extracto de algas marinas, nitrógeno y fósforo en forma asimilable, que provocan la formación y el crecimiento de la raíz.', '/fichasTecnica/Macroroot.pdf', '/hojasSeguridad/Macroroot.pdf', '/FotosProducto/Macroroot.png', '20 x 1 Kg.', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Macroroot.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (39, 'NutriproMag', 'Bionutrientes', 'Fertilizante Inorganico', 'RSCO-095/VIII/17', 'Cloruro de Magnesio (MgCl₂) (30.53%). Líquido soluble.', 'Berries, hortalizas, frutales, gramíneas y ornamentales.', 'Berries: 1 a 2 L/ha. Hortalizas: 2 a 4 L/ha. Frutales: 3 a 4 L/ha. Gramíneas: 2 a 4 L/ha. Ornamentales: 150 a 250 ml/200 L de agua.', 'Consultar etiqueta.', 'Efecto: Es un nutriente vegetal que contiene cloruro de magnesio líquido, que promueve la síntesis de clorofila (pigmento verde fotosintético), la formación de azúcares y mejora la eficiencia de utilización del Nitrógeno, estimulando el crecimiento de los cultivos, su rendimiento y la calidad de los productos cosechados. Aplicado al follaje, al suelo o fertirriego, provee magnesio y cloruro de fácil asimilación por las plantas para el tratamiento de sus deficiencias. Adicionalmente, ayuda a prevenir la acumulación de aminoácidos libres en las plantas que las hacen atractivas para los insectos plagas.', '/fichasTecnica/NutriproMag.pdf', '/hojasSeguridad/NutriproMag.pdf', '/FotosProducto/NutriproMag.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproMag.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (40, 'Delphinus', 'Biocoadyuvantes', 'Biocoadyuvante', 'RSCO-INAC-0104Z-301-375-094', 'Aceite vegetal de maíz. Aceite emulsionable (95.0%)', 'Lima, limonero, mandarina, naranjo, pomelo, tangerina, toronja y nopal.', 'Hortalizas: 0.5 a 2.0 L/100 L agua. Ornamentales: 0.25 a 1.0 L/100 L agua. Frutales: 0.5 a 3.0 L/100 L agua. Aguacatero: 5.0 a 8.0 L/ha. Cítricos: 1.0 a 1.5 L/100 L agua. Manzano: 0.25 a 2.0 L/100 L agua. Papaya: 0.5 a 1.0 L/100 L agua. Plátano: 1.0 L/ha; en aplicación aérea de 3.5 a 5.0 L/ha.', NULL, 'Efecto: Mejora la distribución de los insecticidas sobre la superficie de los vegetales, reduce la tensión superficial de las emulsiones a aplicar, favoreciendo la permanencia y penetración del fitosanitario utilizado, así como una mayor retención del producto sobre la superficie objetivo.', '/fichasTecnica/Delphinus.pdf', '/hojasSeguridad/Delphinus.pdf', '/FotosProducto/Delphinus.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Delphinus.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (41, 'Bio-Stick', 'Biocoadyuvantes', 'Biocoadyuvante', 'RSCO-INAC-0671A-0509-009-95.0', 'Aceite vegetal de ricino (Ricinus communis) etoxilado, diluyente y antiespumante. Concentrado Soluble.', 'Puede mezclarse con otros insumos sin restricción alguna.', '250 a 500 ml/200 L agua. Riego: 1 a 2 L/ha. Usar la dosis mayor en condiciones desfavorables.', NULL, 'Efecto: Es un coadyuvante orgánico, no iónico a base de aceite de soya y de ricino y de disolventes exentos de tolerancias, que encapsula los ingredientes activos, protegiéndolos de la degradación, Además proporciona condiciones favorables en las soluciones de aspersión que contienen microorganismos benéficos, promoviendo su establecimiento y actividad inicial en las plagas blanco.', '/fichasTecnica/BioStick.pdf', '/hojasSeguridad/BioStick.pdf', '/FotosProducto/BioStick.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/BioStick.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (42, 'Oil Premium', 'Biocoadyuvantes', 'Biocoadyuvante', NULL, 'Mezcla de Aceites vegetales y emulsificantes (100.00%). Concentrado emulsionable.', 'Es compatible con captan, carbaril, dimetoato, azufre y fuentes de NPK foliares.', 'Hortalizas 0.7 a 2.5 L/100 L de agua. Frutales: 1 a 3 L/100 L de agua. Plátano: 12 L/ha en aplicación terrestre y 3.5 a 5 L/ha aérea. Cítricos: 10 a 12 L/ha.', NULL, 'Efecto: Es un aceite vegetal diseñado como coadyuvante potencializador de los agroquímicos o bioinsumos con que sea mezclado, ya que les aumenta su efectividad y vida útil en campo, protegiéndolos de los rayos solares. Es indicado para las aplicaciones con insecticidas, fungicidas, herbicidas y acaridas para el control de plagas y enfermedades.', '/fichasTecnica/OilPremium.pdf', '/hojasSeguridad/OilPremium.pdf', '/FotosProducto/OilPremium.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/OilPremium.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (43, 'PineOil', 'Biocoadyuvantes', 'Biocoadyuvante', NULL, 'Mezcla de Pinenos, Aceites vegetales y Emulsificantes (100.00%). Concentrado Soluble.', 'Incompatible con productos de fuerte reacción alcalina. Es necesario realizar una prueba de compatibilidad previa aplicación.', '0.4 a 0.6 L/ha, 1 L/ha en volumen de agua mayor a 800 L/ha.', NULL, 'Efecto: Alarga la vida útil de tus aplicaciones, al mantener adheridos al follaje los productos con que se mezcla. Los encapsula y protege de la radiación solar, por lo que impide su lavado por efecto de la lluvia, además tiene un efecto anti-transpirante que evita choque de trasplante y la deshidratación.', '/fichasTecnica/PineOil.pdf', '/hojasSeguridad/PineOil.pdf', '/FotosProducto/PineOil.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/PineOil.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (44, 'Ionic Ultra', 'Biocoadyuvantes', 'Biocoadyuvante', NULL, 'Mezcla de poliéter polisiloxano modificado, solventes y auxiliares. Concentrado Soluble.', 'Para las mezclas que se realicen con otros productos se recomienda utilizar los que cuenten con registro autorizado y vigente. No presenta cuadros de incompatibilidad debido a que es un coadyuvante.', '40 a 60 ml/100 L agua; con herbicidas, defoliantes y fertilizantes foliares: 50 a 100 ml/100 L de agua.', NULL, 'Efecto: Nueva generación que contiene una mezcla de poliéter polisiloxano modificado, útil para mejorar las características de humectación, extensión y penetración de soluciones que involucran plaguicidas, facilitando la cobertura, y acentuando la efectividad biológica en aplicaciones foliares de soluciones acuosas.', '/fichasTecnica/IonicUltra.pdf', '/hojasSeguridad/IonicUltra.pdf', '/FotosProducto/IonicUltra.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/IonicUltra.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (45, 'BioAdher', 'Biocoadyuvantes', 'Biocoadyuvante', NULL, 'Mezcla de dioctil sulfosuccinato de sodio, poliéter polisiloxano modificado, polioxietilen sorbitán mono laurato, solventes y auxiliares. Concentrado soluble.', 'Su naturaleza química lo hace compatible con otros tensoactivos aniónicos, catiónicos y coloides cargados positiva y negativamente.', '1 a 2 ml/L de agua; herbicidas de translocación y contacto: 3 a 4 ml/L de agua; herbicidas pre-emergentes: 600 a 700 ml/ha.', NULL, 'Efecto: Coadyuvante no iónico que por su acción adherente, humectante, dispersante, facilita un cubrimiento adecuado del follaje y superficies tratadas, de los bio plaguicidas y/o plaguicidas aplicados foliarmente en soluciones acuosas, acentuando su efectividad biológica.', '/fichasTecnica/BioAdher.pdf', '/hojasSeguridad/BioAdher.pdf', '/FotosProducto/BioAdher.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/BioAdher.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES (46, 'BioPH', 'Biocoadyuvantes', 'Biocoadyuvante', NULL, 'Mezcla de ácido cítrico, ácidos grasos etoxilados, auxiliares y disolvente acuoso. Concentrado soluble.', 'Para las mezclas que se realicen con otros productos se recomienda utilizar los que cuenten con registro autorizado y vigente. No presenta cuadros de incompatibilidad debido a que es un coadyuvante.', 'Puede variar dependiendo de la dureza y tipo de agua a utilizar en la aspersión.', NULL, 'Efecto: Coadyuvante no ionico que mejora las características de acidificación, adherencia y humectación de soluciones de aspersión que involucran plaguicidas tales como herbicidas, fungicidas, insecticidas y desecantes. Reduce la tensión superficial del agua, homogeniza las mezclas de plaguicidas y facilita una mejor distribución sobre las superficies tratadas, permitiendo que los activos actúen a su máxima capacidad y evita su degradación prematura.', '/fichasTecnica/BioPH.pdf', '/hojasSeguridad/BioPH.pdf', '/FotosProducto/BioPH.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/BioPH.png');
