INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('24', 'Cyr Ssinal Frut', 'Bionutrientes', 'Bioestimulante', 'RSCO-092/VI/19', 'Brasinolida Líquido soluble (0.01%).', 'Arandano, cerezo, frambuesa, fresa, grosella, mora, zarzamora, berenjena, chile, chile bel, jitomate, papa, tabaco, tomate de cascara, albaricoque, almendra, chabacano, ciruelo, durazno, manzana, membrillo, peral, tejocote, rosa.', '1.5 a 2 mL/L de agua.', 'Foliar: 7 a 21 dias. Fertirriego: 21 a 30 dias', 'Efecto: Regulador de crecimiento extraído de polen que estimula la división celular, elongación celular y actividad fotosintética. Estimula la germinación y promueve el crecimiento de raíces en plántulas y plantas establecidas. Provoca la asimilación, transporte y distribución de los nutrientes hacia los tejidos de la planta estimulando su crecimiento vegetativo, floración, amarre, peso y calidad de los frutos. Prolonga la vida de anaquel de las partes de las cosechas.', '/fichasTecnica/SinalFrut.pdf', '/hojasSeguridad/SinalFrut.pdf', '/FotosProducto/SinalFrut.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/SinalFrut.png');
