INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('28', 'Nutripro Kmg', 'Bionutrientes', 'Biofertilizante Organo Mineral', 'RSCO-163/VII/09', 'Nitrógeno total (N): 1.0%, Potasio (K₂O): 40.0%, Azufre (SO₃): 45.0%, Magnesio (MgO): 2.1%. Polvo.', 'Jitomate, chile, sandía, melón, calabacita, pepino, fresa, papa y ornamentales.', 'Foliar: 4.0 a 6.0 g/L agua. Fertirriego: 5 a 10 kg/ha.', 'Foliar: Cada 7–14 días. Fertirriego: A partir de trasplante.', 'Efecto: Fertilizante organo-mineral en polvo que contiene nitrógeno, potasio, azufre, magnesio, aminoácidos y materia orgánica. Previene y corrige los síntomas causados por la deficiencia de nitrógeno, potasio, azufre y magnesio. Los aminoácidos totales y materia orgánica facilita la penetración y translocación de los nutrientes al interior de la planta, ayudando al cultivo en el desarrollo vegetativo y favorece el amarre y cuajado de los frutos.', '/fichasTecnica/NutriproKMg.pdf', '/hojasSeguridad/NutriproKMg.pdf', '/FotosProducto/NutriproKMg.png', '20 x 1 Kg.', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproKMg.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('29', 'Nutripro CaB', 'Bionutrientes', 'Biofertilizante Organo Mineral', 'RSCO-222/XI/09', 'Calcio (CaO): 9.0%, Magnesio (MgO): 2.0%, Boro (B): 1.0%, Azufre (S): 3.5%. Líquido.', 'Jitomate, chile, sandía, melón, calabacita, pepino, fresa, papa, lechuga, col, coliflor, vid, algodón, cítricos, manzano, durazno, ciruelo, peral, aguacate, maíz y trigo.', 'Foliar: 2.5 a 3.5 ml/L de agua. Fertirriego: 2.0 a 4.0 L/ha.', '7 a 14 días.', 'Efecto: Es un fertilizante organo-mineral líquido que contiene nitrógeno, calcio,magnesio, boro y aminoácidos libres. Por su contenido de calcio ayuda directamente en el crecimiento apical, consistencia de la estructura de la planta y la calidad de los frutos después del amarre., previene y corrige los síntomas causados por la deficiencia de nitrógeno, calcio, magnesio y boro, los aminoácidos libres ayudan a que la planta asimile los nutrientes disponibles, ayuda al cultivo en cuajado y desarrollo del fruto.', '/fichasTecnica/NutriproCaB.pdf', '/hojasSeguridad/NutriproCaB.pdf', '/FotosProducto/NutriproCaB.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproCaB.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('30', 'Nutripro Energy', 'Bionutrientes', 'Bioestimulante', 'RSCO-146/XII/20', 'Nitrógeno Orgánico: (16.00%). Carbono Orgánico: (44.00%). Micro-gránulos hidrosolubles.', 'Aguacate berenjena, chile, jitomate, okra, papa, pimiento morrón, tomate, arándano, frambuesa, fresa, grosella, zarzamora, zarzaparrilla, calabaza, calabacita, chayote, melón, pepino, sandía.', '5 a 20 kg/ha.', 'Realizar dos aplicaciones dirigidas al suelo en la etapa de desarrollo vegetativo y floración.', 'Efecto: Nutriente Vegetal formulado en microgránulos altamente solubles en agua que contiene nitrógeno orgánico y aminoácidos activos que permiten una respuesta rápida de los cultivos. Está diseñado para aportar nitrógeno en las etapas de mayor demanda del cultivo, para estimular el desarrollo radicular y vegetal, así como el calibre y calidad de los frutos.', '/fichasTecnica/NutriproEnergy.pdf', '/hojasSeguridad/NutriproEnergy.pdf', '/FotosProducto/NutriproEnergy.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproEnergy.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('31', 'Aminofit KBorCa', 'Bionutrientes', 'Fertilizante Foliar', 'RSCO-222/XI/09', 'Calcio (Ca-EDTA): 5.31%, Boro (B): 1.43%, Potasio (K2O) 13.38%, Nitrógeno total (N): 3.26%. Líquido soluble.', 'Berries, hortalizas, frutales, granos, forrajes y ornamentales.', 'Berries y Hortalizas 1 - 2 L/ha. Frutales 2- 4 L/ha. Granos y Forrajes 1 – 1.5 L/ha. Ornamentales 50 – 70 ml/100 L de agua.', '7 a 14 días.', 'Efecto: Es un nutriente vegetal que contiene calcio quelado, boro, potasio y nitrógeno, recomendado para prevenir trastornos fisiológicos asociados con su deficiencia. Promueve vigor en las plantas, tallos fuertes, reduce el aborto de flores y frutos, promueve la floración y el amarre de frutos. Mejora la calidad de los frutos , maduración, firmeza y vida de anaquel.', '/fichasTecnica/KBorCa.pdf', '/hojasSeguridad/KBorCa.pdf', '/FotosProducto/KBorCa.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/KBorCa.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('32', 'Aminofit.Xtra', 'Bionutrientes', 'Aminoacidos', 'RSCO-075/XI/04', 'Aminoácidos: Glicina 800 mg, Valina 40 mg, Prolina 350 mg, Hidroxiprolina 300 mg, Alanina 350 mg, Ác. Aspártico 680 mg, Arginina 80 mg, Ác. Glutámico 225 mg, Lisina 345 mg, Leucina 50 mg, Isoleucina 125 mg, Fenilalanina 145 mg, Metionina 25 mg, Serina 140 mg, Asparagina 125 mg, Treonina 120 mg, Cistina y Cisteina 85 mg, Tirosina 88 mg, Histidina 102 mg. Solución Acuosa.', 'Frutales, ornamentales y hortalizas.', 'Vía foliar: 2.5-5 L/ha. Suelo: 5-15 L/ha/Ciclo', 'Durante todo el ciclo del cultivo y de 2 a 3 semanas.', 'Efecto: Síntesis de proteínas que desarrollan funciones estructurales, enzimáticas y hormonales, relacionadas con los mecanismos de regulación del crecimiento y desarrollo vegetal. Forma quelatos con microelementos favoreciendo su transporte y asimilación. Recupera daños en cultivos.', '/fichasTecnica/Aminoacidos.pdf', '/hojasSeguridad/Aminoacidos.pdf', '/FotosProducto/Aminoacidos.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Aminoacidos.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('33', 'Progrow Activador', 'Bionutrientes', 'Regulador de crecimiento', 'RSCO-150/VIII/19', 'Ácido giberélico 1,451.00 ppm, Ácido-3-indolacético 4,070.00 ppm, Ácido glutámico 1,117.00 ppm, Aminoácidos 0.737 ppm, Niacina 1,048.10 ppm, Tiamina 1,019.27 ppm, Polisacáridos, Glucosa 2,051.70 ppm, Fructosa 1,798.50 ppm, Galactosa 279.48 ppm, Fósforo (P₂O₅) 1,052.10 ppm, Potasio (K₂O) 876.20 ppm, Nitrógeno total (N) 12.21%. Líquido soluble.', 'Calabaza, calabacita, chayote, melón, pepino, sandía, berenjena, chile, chile bel, jitomate, papa, tabaco, tomate de cáscara.', '500 – 750 mL/ha', 'Aplicaciones cada 15 días desde la aparición del botón floral hasta el llenado de frutos.', 'Efecto: Es un regulador de crecimiento que interviene en los procesos metabólicos y fisiológicos de las plantas: división celular, síntesis de clorofila, aporte de energía y sustancias nutritivas. Aplíquelo para estimular la condición nutrimental de la planta, el volumen radicular, la altura de planta, biomasa seca de la planta', '/fichasTecnica/Activador.pdf', '/hojasSeguridad/Activador.pdf', '/FotosProducto/Activador.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Activador.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('34', 'Progrow Macroroot', 'Bionutrientes', 'Regulador de crecimiento', 'RSCO-030/IV/20', 'Ácido Naftalenacético 2812 ppm, Ácido Indolbutírico 209 ppm, Extracto de Algas 5.243% p/p, Fósforo (P₂O₅) 55.108% p/p, Nitrógeno total (N) 4.72% p/p. Polvo soluble.', 'Plátano, banano, berenjena, chile, chile bel, jitomate, papa, tabaco, tomate de cáscara.', '1.0 – 1.5 kg/ha', '7 días en plántulas a partir de la emergencia o trasplante; 15 y 30 días en plantas establecidas.', 'Efecto: Es un regulador de crecimiento que contiene fitohormonas, extracto de algas marinas, nitrógeno y fósforo en forma asimilable, que provocan la formación y el crecimiento de la raíz.', '/fichasTecnica/Macroroot.pdf', '/hojasSeguridad/Macroroot.pdf', '/FotosProducto/Macroroot.png', '20 x 1 Kg.', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Macroroot.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('35', 'Nutripro Mag', 'Bionutrientes', 'Fertilizante Inorganico', 'RSCO-095/VIII/17', 'Cloruro de Magnesio (MgCl₂) (30.53%). Líquido soluble.', 'Berries, hortalizas, frutales, gramíneas y ornamentales.', 'Berries: 1 a 2 L/ha. Hortalizas: 2 a 4 L/ha. Frutales: 3 a 4 L/ha. Gramíneas: 2 a 4 L/ha. Ornamentales: 150 a 250 ml/200 L de agua.', 'Consultar etiqueta.', 'Efecto: Es un nutriente vegetal que contiene cloruro de magnesio líquido, que promueve la síntesis de clorofila (pigmento verde fotosintético), la formación de azúcares y mejora la eficiencia de utilización del Nitrógeno, estimulando el crecimiento de los cultivos, su rendimiento y la calidad de los productos cosechados. Aplicado al follaje, al suelo o fertirriego, provee magnesio y cloruro de fácil asimilación por las plantas para el tratamiento de sus deficiencias. Adicionalmente, ayuda a prevenir la acumulación de aminoácidos libres en las plantas que las hacen atractivas para los insectos plagas.', '/fichasTecnica/NutriproMag.pdf', '/hojasSeguridad/NutriproMag.pdf', '/FotosProducto/NutriproMag.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/NutriproMag.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('36', 'Cyr Zn 70', 'Bionutrientes', 'Bionutriente', 'RSCO-063/II/13', 'Zinc (Zn): 70.00 %.', 'Café, papa, hortalizas, aguacate, frutales, plátano, algodón, maíz, arroz, caña de azúcar.', '0.5 - 1 l/ha', 'Aplicaciones al inicio del ciclo de desarrollo del cultivo o como tratamiento de semilla.', 'Efecto: Es una emulsión de alta concentración de un solo elemento para corregir deficiencias específicas. Se formula con potenciadores para mejorar la asimilación y la acción de CYR Zn 70 con el tiempo. El Zinc juega un papel importante como estimulante metabólico en semillas y en plantas jóvenes donde activa las enzimas claves para el crecimiento radicular. Participa en la conversión de aminoácidos en polipéptidos, proteínas y hormonas tal como auxinas, producidas en las puntas de crecimiento de los tallos y posteriormente traslocadas a la raíz para estimular el crecimiento radicular. El Zinc también juega un papel importante en las modificaciones constantes de la integridad de la membrana celular y así ayuda a la planta a superar el estrés debido a la producción de radicales libres en el proceso de fotosíntesis.', '/fichasTecnica/Zn70.pdf', '/hojasSeguridad/Zn70.pdf', '/FotosProducto/Zn70.png', '12 x 1 L, 1 x 5 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Zn70.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('37', 'Cyr Dp 98', 'Bionutrientes', 'Bionutriente', 'RSCO-064/II/13', 'Nitrógeno (N): 4.0%, Fósforo (P2O5): 37.8%, Potasio (K2O): 17.5%.', 'Lechuga, papa, brasicáceas, cebolla, poro, zanahoria, manzana, frambuesa, fresa.', '2-4 l/ha', 'Cada 14 a 28 días según se necesite.', 'Efecto: Es una fuente de fósforo altamente soluble en agua. La alta solubilidad de DP98 permite una rápida captura y absorción de fósforo cuando se aplica foliarmente. El fósforo en productos convencionales se mueve en la planta por el xilema. Sin embargo, en DP98 el fósforo se mueve por el floema y xilema llegando entonces a la zona de las raíces y a la parte aérea.', '/fichasTecnica/Dp98.pdf', '/hojasSeguridad/Dp98.pdf', '/FotosProducto/Dp98.png', '12 x 1 L, 1 x 5 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Dp98.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('38', 'Cyr ZynQ', 'Bionutrientes', 'Bionutriente', 'RSCO-413/VIII/23', 'Cobre (Cu): 2.11%, Zinc (Zn): 3.75%, Azufre (SO3): 7.40%', 'Zanahoria y otros cultivos de raíz, cereales, legumbres, papa, cultivos de invernadero y verduras, verduras en campo abierto, berries, árboles frutales, vid', '0.5-1 l/ha', '7-14 días', 'Efecto:  Un complejo único de cobre y de zinc. CYR ZynQ corrige deficiencias de estos elementos, mejora la salud vegetal y vuelve el cultivo más tolerante al estrés abiótico. El Zinc participa en la producción y activación de hormonas que regulan el desarrollo radicular, la asimilación de agua por la raíz, la floración y el amarre de frutas. Además, juega un papel importante en la permeabilidad de la membrana celular y la defensa del cultivo contra enfermedades. El cobre participa en la fotosíntesis y la unión de células que apoyan la estructura de la planta y el desarrollo de tejidos nuevos tanto en la raíz como en el tallo. El cobre está también involucrado en la activación de múltiples sistemas de enzimas relacionados con el metabolismo de proteínas, de carbohidratos y de nitrógeno. El cobre forma parte de los compuestos fenólicos que ayudan a la planta a defenderse contra enfermedades.', '/fichasTecnica/ZynQ.pdf', '/hojasSeguridad/ZynQ.pdf', '/FotosProducto/ZynQ.png', '12 x 1 L, 1 x 5 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/ZynQ.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('39', 'Cyr K41', 'Bionutrientes', 'Bionutriente', 'RSCO-161/VIII/18', 'Nitrógeno total (N): 2.00%, Potasio (K2O): 26.40%, Magnesio (MgO): 1.60%, Azufre (S): 6.00%.', 'Almendra, manzana, albaricoque, aguacate, pepino, melón, pimiento, tomate, papa, uva de mesa', '3 - 5 lt /ha', '2 a 3 aplicaciones desde floración hasta formación de frutos o tubérculos.', 'Efecto: Es un fertilizante fluido y totalmente soluble en agua que contiene potasio, magnesio y azufre. El potasio es un macro elemento que se necesita en grandes cantidades para el desarrollo normal de la planta. Está implicado en el metabolismo de la planta y en regular las reservas de agua. Otra función importante es que regula el transporte en el estoma. El potasio interviene en la fotosíntesis y está implicado en el transporte de azúcares en la hoja.', '/fichasTecnica/K41.pdf', '/hojasSeguridad/K41.pdf', '/FotosProducto/K41.png', '12 x 1 L, 1 x 5 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/K41.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('40', 'Cyr Bio 20', 'Bionutrientes', 'Bionutriente', 'RSCO-062/II/13', 'Nitrógeno total, (N): 20.00% , Fósforo (P2O5): 20.00%, Potasio (K2O): 20.00%, Oxido de Magnesio (MgO): 1.50%, Hierro (EDTA) (Fe 0):0.146%, Zinc (EDTA) (Zn): 0.073%, Cobre (EDTA) (Cu): 0.073%, Manganeso (EDTA) (Mn): 0.073%, Boro (B): 0.029%, Cobalto (EDTA) (Co): 0.0012%, Molibdeno (Mo): 0.0012%, Extracto de algas: 28.00%.', 'Remolacha, papa, cereales, hortalizas, frutales, cítricos, ornamentales', '1-3 l/ha', '15 días', 'Efecto: Es una emulsión altamente concentrada que contiene macroelementos y microelementos quelatados. El material orgánico proviene de un extracto de algas. Maximiza el potencial de producción de cultivos que tienen peligro de daños por condiciones de estrés, tales como altas temperaturas, determinadas condiciones de suelo, accesibilidad al agua y enfermedades.', '/fichasTecnica/Bio20.pdf', '/hojasSeguridad/Bio20.pdf', '/FotosProducto/Bio20.png', '12 x 1 L, 1 x 5 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/Bio20.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('41', 'Cyr Calmax Super', 'Bionutrientes', 'Bionutriente', 'RSCO-065/II/13', 'Nitrógeno (N): 16.00%, Calcio (CaO): 24.00%, Magnesio (MgO): 3.20%, Manganeso (EDTA) (Mn): 0.16%, Hierro (EDTA) (Fe): 0.16%, Boro (B): 0.080%, Cobre (EDTA) (Cu): 0.080%, Zinc (EDTA) (Zn) 0.080%, Molibdeno (Mo): 0.016%.', 'Frutales (tanto de hueso como de pepita), uva, tomate, pepino, calabaza, berenjena habichuela, fresa, coles, alcachofa, lechuga, papa, melón, cítricos, algodón.', '2.5 L/ha', '2 ó 3 aplicaciones desde el inicio de la floración a intervalos de 8 – 20 días.', 'Efecto: Es una emulsión fluida totalmente soluble en agua con 22.5% de calcio y una gama completa de microelementos. Calmax Super contiene todos los aminoácidos esenciales para la planta. El calcio es un constituyente primario de las paredes celulares y de las membranas. El componente Super aporta aminoácidos a la formulación de Calmax. Son bioactivadores del metabolismo de la planta especialmente indicados para superar situaciones de estrés tales como frío, heladas, enfermedades o calor y para estimular la planta en las etapas críticas del ciclo biológico : floración, cuajado y fructificación. Calmax Super potencia la actividad de la flor y la fruta aumentando su capacidad de asimilación de azúcares y otros productos de la fotosíntesis para su posterior desarrollo, engrosamiento y maduración. Calmax Super ayuda a aumentar el rendimiento y la calidad de la cosecha.', '/fichasTecnica/CalmaxSuper.pdf', '/hojasSeguridad/CalmaxSuper.pdf', '/FotosProducto/CalmaxSuper.png', '12 x 1 L, 1 x 5 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/CalmaxSuper.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('42', 'Bio-Stick', 'Biocoadyuvantes', 'Bioadherente', 'RSCO-INAC-0671A-0509-009-95.0', 'Aceite vegetal de ricino (Ricinus communis) etoxilado, diluyente y antiespumante. Concentrado Soluble.', 'Puede mezclarse con otros insumos sin restricción alguna.', '250 a 500 ml/200 L agua. Riego: 1 a 2 L/ha. Usar la dosis mayor en condiciones desfavorables.', NULL, 'Efecto: Es un coadyuvante orgánico, no iónico a base de aceite de soya y de ricino y de disolventes exentos de tolerancias, que encapsula los ingredientes activos, protegiéndolos de la degradación, Además proporciona condiciones favorables en las soluciones de aspersión que contienen microorganismos benéficos, promoviendo su establecimiento y actividad inicial en las plagas blanco.', '/fichasTecnica/BioStick.pdf', '/hojasSeguridad/BioStick.pdf', '/FotosProducto/BioStick.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/BioStick.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('43', 'Progranic Oil Premium', 'Biocoadyuvantes', 'Potencializador', NULL, 'Mezcla de Aceites vegetales y emulsificantes (100.00%). Concentrado emulsionable.', 'Es compatible con captan, carbaril, dimetoato, azufre y fuentes de NPK foliares.', 'Hortalizas 0.7 a 2.5 L/100 L de agua. Frutales: 1 a 3 L/100 L de agua. Plátano: 12 L/ha en aplicación terrestre y 3.5 a 5 L/ha aérea. Cítricos: 10 a 12 L/ha.', NULL, 'Efecto: Es un aceite vegetal diseñado como coadyuvante potencializador de los agroquímicos o bioinsumos con que sea mezclado, ya que les aumenta su efectividad y vida útil en campo, protegiéndolos de los rayos solares. Es indicado para las aplicaciones con insecticidas, fungicidas, herbicidas y acaridas para el control de plagas y enfermedades.', '/fichasTecnica/OilPremium.pdf', '/hojasSeguridad/OilPremium.pdf', '/FotosProducto/OilPremium.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/OilPremium.png');
