INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('52', 'Prolux Plus pH', 'Biocoadyuvantes', 'Coadyuvante', NULL, 'Mezcla de Ácido cítrico, Alcohol etoxilado C8–C10.5EO y disolvente acuoso (100.00%). Concentrado soluble.', 'Puede mezclarse con cualquier tipo de plaguicida registrado sin restricción alguna. Este material no precipita en presencia de iones de dureza de agua.', 'Agregue la dosis requerida para acidificar el volumen de agua total del contenedor. Siga las instrucciones de la etiqueta.', NULL, 'Efecto: Es un coadyuvante no iónico que mejora las características de adherencia y acidificación de soluciones de aspersión. Reduce la tensión superficial del agua, homogeniza las mezclas de plaguicidas y facilita una mejor distribución sobre las superficies tratadas, permitiendo que los activos actúen a su máxima  capacidad y evita su degradación', '/fichasTecnica/ProluxPlusPh.pdf', '/hojasSeguridad/ProluxPlusPh.pdf', '/FotosProducto/ProluxPlusPh.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/ProluxPlusPh.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('53', 'Prowet CL', 'Biocoadyuvantes', 'Coadyuvante', NULL, 'Mezcla de Aceites vegetales sulfonados, Aminoácidos, Agentes tensioactivos y Disolvente acuoso (100.00%). Concentrado soluble.', 'Puede mezclarse con cualquier tipo de plaguicida registrado sin restricción alguna.', '2 a 3 L/ha en el agua de riego/ha y de 1 a 1.5 L/ha /1000 L de solución madre en riego por goteo. A mayor dosis mayor cobertura.', NULL, 'Efecto: Reduce la tensión superficial del agua, permitiendo que los ingredientes activos de los insumos fitosanitarios se muevan más rápido en dirección vertical (penetraciòn) y horizontal (dispersión) en suelos arcillosos o subsuelo denso evitando la escorrentía sin deteriorar la estructura del suelo. Promueve la extensión del bulbo de humedecimiento en las capas profundas del perfil del suelo haciendo disponible el agua para las raíces de las plantas.', '/fichasTecnica/ProwetCL.pdf', '/hojasSeguridad/ProwetCL.pdf', '/FotosProducto/ProwetCL.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/ProwetCL.png');
INSERT INTO `productos` (`id`, `nombre`, `segmento`, `categoria`, `registro`, `contenido`, `usorecomendado`, `dosissugerida`, `intervaloaplicacion`, `controla`, `fichatecnica`, `hojaseguridad`, `fotoproducto`, `presentacion`, `creadopor`, `modificadopor`, `fechacreacion`, `fechaactualizacion`, `fotocatalogo`) VALUES ('54', 'Prowet SA', 'Biocoadyuvantes', 'Coadyuvante', NULL, 'Aceites sulfonados + saponinas + aminoácidos + agentes. Concentrado soluble.', 'Contiene azúcares, aminoácidos naturales, materia orgánica y ácidos húmicos que estimulan el crecimiento radicular y promueven la flora microbiana, con el enriquecimiento paulatino de la textura y estructura del suelo.', 'Es de 2 a 3 L/ha en el agua de riego y de 1 a 1.5 L/ha por cada 1000 L.', NULL, 'Efecto: Es un coadyuvante que mejora la extensión horizontal y retención vertical en el perfil de suelo de las soluciones acuosos que contengan nutrientes vegetales, fertilizantes, reguladores de crecimiento y plaguicidas solubles. Reduce la tensión superficial del agua permitiendo la extensión del bulbo de humedecimiento en el  estrato superior del suelo en donde se localiza la zona radicular de los cultivos, mejorando la absorción de los plaguicidas sistémicos y de las soluciones nutritivas en el menor tiempo de suministro de agua, evitando su escorrentía y lixiviación.', '/fichasTecnica/ProwetSA.pdf', '/hojasSeguridad/ProwetSA.pdf', '/FotosProducto/ProwetSA.png', '12 x 1 L, 1 x 20 L', 'Administrador', NULL, NULL, NULL, '/FotosCatalogo/ProwetSA.png');
